var checkoutForm=jQuery("form.checkout");function setLoadingDone(){checkoutForm.removeClass("processing").unblock()}function setLoadingOn(){checkoutForm.addClass("processing").block({message:null,overlayCSS:{background:"#fff",opacity:.6}})}function dataCallbackFunction(data){console.log("dataCallbackFunction:",data)}function errorCallback(code,string){console.log("errorCallback: ",code,string)}function getAjaxURL(endpoint){return woocommerce_fastspring_params.ajax_url.toString().replace("%%endpoint%%","wc_fastspring_"+endpoint)}function fastspringBeforeRequestHandler(){setLoadingDone()}function fastspringPopupCloseHandler(data){if(data&&data.reference){requestPaymentCompletionUrl(data||{},function(err,res){if(!err){window.location=res.redirect_url}})}}function requestPaymentCompletionUrl(data,cb){var nonce={};jQuery.ajax({type:"POST",dataType:"json",data:{action:"wc-fastspring-generate-nonce"},url:"/wp-admin/admin-ajax.php",success:function(response){nonce=response.nonce},async:false});data.security=nonce.receipt;jQuery.ajax({type:"POST",dataType:"json",data:JSON.stringify(data),url:getAjaxURL("get_receipt"),success:function(response){cb(null,response)},error:function(xhr,err,e){cb(xhr.responseText)}})}function launchFastSpring(session){console.log(session);fastspring.builder.secure(session.payload,session.key);fastspring.builder.checkout()}function setOrder(cb){jQuery.ajax({type:"POST",url:wc_checkout_params.checkout_url,data:checkoutForm.serialize(),dataType:"json",success:function(result){try{if(result.result==="success"){cb(null,result)}else if(result.result==="failure"){throw new Error("Result failure")}else{throw new Error("Invalid response")}}catch(err){if(result.reload===true){window.location.reload();return}if(result.refresh===true){jQuery(document.body).trigger("update_checkout")}if(result.messages){submitError(result.messages)}else{submitError('<div class="woocommerce-error">'+wc_checkout_params.i18n_checkout_error+"</div>")}}},error:function(jqXHR,textStatus,errorThrown){submitError('<div class="woocommerce-error">'+errorThrown+"</div>")}})}function doSubmit(){setLoadingOn();setOrder(function(err,result){if(!err){launchFastSpring(result.session)}})}function submitError(errorMessage){setLoadingDone();jQuery(".woocommerce-NoticeGroup-checkout, .woocommerce-error, .woocommerce-message").remove();checkoutForm.prepend('<div class="woocommerce-NoticeGroup woocommerce-NoticeGroup-checkout">'+errorMessage+"</div>");checkoutForm.removeClass("processing");checkoutForm.find(".input-text, select, input:checkbox").trigger("validate").blur();jQuery("html, body").animate({scrollTop:jQuery("form.checkout").offset().top-100},1e3);jQuery(document.body).trigger("checkout_error")}function isFastSpringSelected(){return jQuery('.woocommerce-checkout input[name="payment_method"]:checked').attr("id")==="payment_method_fastspring"}checkoutForm.on("checkout_place_order",function(){if(isFastSpringSelected()){doSubmit();return false}});